import { useState } from "react";
import { FaFileImage, FaFilePdf } from "react-icons/fa"; // Icons for image and PDF
import "./Display.css";

const Display = ({ contract, account }) => {
  const [data, setData] = useState("");

  const getdata = async () => {
    let dataArray;
    const Otheraddress = document.querySelector(".address").value;

    try {
      if (Otheraddress) {
        dataArray = await contract.display(Otheraddress);
        console.log(dataArray);
      } else {
        dataArray = await contract.display(account);
      }
    } catch (e) {
      alert("You don't have access");
      return;
    }

    const isEmpty = Object.keys(dataArray).length === 0;

    if (!isEmpty) {
      const str = dataArray.toString();
      const str_array = str.split(",");

      const items = str_array.map((item, i) => {
        const url = `https://gateway.pinata.cloud/ipfs/${item.substring(6)}`;

        // Determine if it's an image or PDF based on file extension
        const isImage = /\.(jpg|jpeg|png|gif)$/i.test(url);
        const isPdf = /\.pdf$/i.test(url);

        return (
          <a href={url} key={i} target="_blank" rel="noopener noreferrer" className="file-link">
            {isImage ? (
              <FaFileImage size={40} color="blue" /> // Icon for image
            ) : isPdf ? (
              <FaFilePdf size={40} color="red" /> // Icon for PDF
            ) : (
              <span>Unknown file type</span> // Default for unknown file types
            )}
            <span className="file-name">{item}</span>
          </a>
        );
      });
      setData(items);
    } else {
      alert("No files to display");
    }
  };

  return (
    <>
      <div className="file-list">{data}</div>
      <input type="text" placeholder="Enter Address" className="address" />
      <button className="center button" onClick={getdata}>
        Get Data
      </button>
    </>
  );
};

export default Display;



